import React, { useState } from 'react';
import LaunchList from './LaunchList';

const LaunchListContainer = () => {
  const [sortKey, setSortKey] = useState('');
  const [launches, setLaunches] = useState([]);

  const handleSort = key => {
    setSortKey(key);
  };

  const fetchLaunches = () => {
    fetch('https://api.spacexdata.com/v4/launches')
      .then(response => response.json())
      .then(data => setLaunches(data))
      .catch(error => console.error('Error fetching launches:', error));
  };

  const sortLaunches = (launches, key) => {
    return [...launches].sort((a, b) => {
      if (a[key] < b[key]) return -1;
      if (a[key] > b[key]) return 1;
      return 0;
    });
  };

  useEffect(() => {
    fetchLaunches();
  }, []);

  let sortedLaunches = [...launches];
  if (sortKey === 'flight_number') {
    sortedLaunches = sortLaunches(sortedLaunches, 'flight_number');
  } else if (sortKey === 'name') {
    sortedLaunches = sortLaunches(sortedLaunches, 'name');
  } else if (sortKey === 'launch_date') {
    sortedLaunches = sortLaunches(sortedLaunches, 'date_utc');
  }

  return (
    <div>
      <h1>Launch List Container</h1>
      <button onClick={() => handleSort('flight_number')}>Sort by Flight Number</button>
      <button onClick={() => handleSort('name')}>Sort by Name</button>
      <button onClick={() => handleSort('launch_date')}>Sort by Launch Date</button>
      <LaunchList launches={sortedLaunches} />
    </div>
  );
};

export default LaunchListContainer;
